import React from 'react';
// 导入antd
import { Button } from 'antd';
import 'antd/dist/antd.css';
// 导入redux相关
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
// 导入所有title action creator
import * as titleActionCreator from '../../action/titleAction';

/**
 * Title组件
 * @author pangchun
 * @since  2021-9-29
 */
class Title extends React.Component {
    constructor() {
        super(...arguments);
        this.state = {
            buttonName: '点击改变标题名称',
        };
    }

    handleChange = () => {
        console.log(this.props.titleName);
    }

    render() {
        return (
            <div className="title">
                <h1 className="titleName">欢迎来到{this.props.titleName}</h1>
                <Button type="primary" onClick={this.handleChange}>{this.state.buttonName}</Button>
            </div>
        );
    }
}

export default Title;

const mapStateToProps = (state) => ({
    title: state.title, // 这里为什么可以这样写？请看
    // 还有其它
})
